import { reqGetSearchInfo } from "@/api";
//search模块的小仓库
const state = {
  //仓库初始状态
  searchList:{},
};
const mutations = {
  //修改state状态
  GetSAERCHLIST(state, searchList) {
    state.searchList = searchList;
  },
};
const actions = {
  //获取数据
  async getSearchList({ commit }, parmas = {}) {
    let result = await reqGetSearchInfo(parmas);
    console.log(111);
    if (result.code == 200) {
      commit("GetSAERCHLIST", result.data);
    }
  },
};
const getters = {};
export default {
  state,
  mutations,
  actions,
  getters,
};
