//配置路由
import Vue from "vue";
import VueRouter from "vue-router";

//使用插件
Vue.use(VueRouter);

//引入路由组件
import Home from "@/pages/Home";
import Login from "@/pages/Login";
import Resgister from "@/pages/Resgister";
import Search from "@/pages/Search";

//暴露路由
export default new VueRouter({
  //配置路由
  routes: [
    {
      path: "/home",
      component: Home,
    },
    {
      path: "/login",
      component: Login,
    },
    {
      path: "/resgister",
      component: Resgister,
    },
    {
      path: "/search",
      component: Search,
    },
    //重定向,在项目跑起来的时候，访问/，立马dingxiangdao

  ],
});
