//配置路由
import Vue from "vue";
import VueRouter from "vue-router";

//使用插件
Vue.use(VueRouter);

//引入路由组件
import Home from "@/pages/Home";
import Login from "@/pages/Login";
import Resgister from "@/pages/Resgister";
import Search from "@/pages/Search";

//暴露路由
export default new VueRouter({
  //配置路由
  routes: [
    {
      path: "/home",
      component: Home,
      meta:{show:true},
    },
    {
      path: "/login",
      component: Login,
      meta:{show:false},
    },
    {
      path: "/resgister",
      component: Resgister,
      meta:{show:false},
    },
    {
      path: "/search/:keyword",
      component: Search,
      meta:{show:true},
      name:"search",
      //路由组件可以传递props参数(三种写法：布尔，对象，函数)
      props
    },
    //重定向,在项目跑起来的时候，访问/，立马定向到您所需的页面(首页)
    {
      path: "*",
      redirect: "/home",
    },
  ],
});
