import Vue from "vue";
import Vuex from "vuex";

//使用vuex
Vue.use(Vuex);

//state:仓库存储数据的地方
const state = {
  count: 1,
};
//mutations:修改state的唯一手段
const mutations = {};
//action：处理action，可以书写自己的业务，也能处理异步
const actions = {
  //这里可以书写业务逻辑，但是不能修改state
  add(commit) {
    commit("ADD");
  },
};
//getters：理解为计算属性，用于简化仓库，让组件获取仓库数据更加方便
const getters = {};

//对外暴露store累的一个实例
export default new Vuex.Store({
  state,
  mutations,
  actions,
  getters,
});
